import{_ as a}from"./plugin-vue_export-helper-c27b6911.js";import{r as t,o as s,c as l,e as r,a as e,d,w as n,b as o,f as i}from"./app-d6438571.js";const _="/assets/elk-d2fca87b.jpg",h={},u=e("p",null,[o("ELK，是Elasticsearch（搜索引擎）, Logstash（日志收集），Kibana（可视化的Web界面）的组合。"),e("br"),e("img",{src:_,alt:"",loading:"lazy"})],-1),m={class:"table-of-contents"},f=i('<h2 id="elk的简介" tabindex="-1"><a class="header-anchor" href="#elk的简介" aria-hidden="true">#</a> ELK的简介</h2><blockquote><p>ELK是三个开源软件的缩写，分别表示：<code>elasticsearch</code>、<code>logstash</code>、<code>kibana</code></p></blockquote><ul><li><code>Elasticsearch</code>是个开源分布式搜索引擎，提供搜集、分析、存储数据三大功能。它的特点有：分布式，零配置，自动发现，索引自动分片，索引副本机制，restful风格接口，多数据源，自动搜索负载等</li><li><code>Logstash</code> 主要是用来日志的搜集、分析、过滤日志的工具，支持大量的数据获取方式。一般工作方式为c/s架构，client端安装在需要收集日志的主机上，server端负责将收到的各节点日志进行过滤、修改等操作在一并发往<code>elasticsearch</code>上去。</li><li><code>Kibana</code>可以为 <code>Logstash</code> 和 <code>ElasticSearch</code> 通过报表、图形化数据进行可视化展示 Web 界面，可以帮助汇总、分析和搜索重要数据日志</li></ul>',3);function b(p,k){const c=t("router-link");return s(),l("div",null,[u,r(" more "),e("nav",m,[e("ul",null,[e("li",null,[d(c,{to:"#elk的简介"},{default:n(()=>[o("ELK的简介")]),_:1})])])]),f])}const K=a(h,[["render",b],["__file","50.ELK应用.html.vue"]]);export{K as default};
